actions
(
    {
        name    cyclicFacesFaceSet;
        type    faceSet;
        action  new;
        source  boxToFace;
        box     (0.099 -0.006 0.004)(0.101 0.006 0.016);
    }

    {
        name    cyclicFacesSlaveCells;
        type    cellSet;
        action  new;
        source  boxToCell;
        box     (-10 -10 -10)(0.1 10 10);
    }

    {
        name    cyclicFaces;
        type    faceZoneSet;
        action  new;
        source  setsToFaceZone;
        faceSet cyclicFacesFaceSet;
        cellSet cyclicFacesSlaveCells;
    }

    {
        name    baffleFaceSet;
        type    faceSet;
        action  new;
        source  boxToFace;
        box     (0.099 -10 -10)(0.101 10 10);
    }

    {
        name    baffleFaceSet;
        type    faceSet;
        action  delete;
        source  boxToFace;
        box     (0.099 -0.006 0.004)(0.101 0.006 0.016);
    }

    {
        name    baffleFaces;
        type    faceZoneSet;
        action  new;
        source  setToFaceZone;
        faceSet baffleFaceSet;
    }
);

